class BindInst : public MainLoopItem
{
public:
  BindInst(GameApi::Env &env, Material *mat, GameApi::P p, GameApi::PTS pts) : env(env), mat(mat),p(p),pts(pts) { ml.id = -1; firsttime = true; }
  void find_ml() {
    if (ml.id==-1) {
      ml.id = mat->mat_inst(p.id,pts.id);
    }
  }
  void Collect(CollectVisitor &vis)
  {
      find_ml();
      MainLoopItem *item = find_main_loop(env,ml);
      item->Collect(vis);
      vis.register_obj(this);
      firsttime = false;
  }
  void HeavyPrepare() {
    FaceCollection *coll = find_facecoll(env,p);
    if (coll->NumFaces()==0) disabled=true;
  }
  virtual void Prepare()
  {
    FaceCollection *coll = find_facecoll(env,p);
    if (firsttime) {
      find_ml();
      MainLoopItem *item = find_main_loop(env,ml);
      item->Prepare();
      HeavyPrepare();
      firsttime = false;
    }
  }
  virtual void execute(MainLoopEnv &e)
  {
    if (disabled) return;
    if (firsttime) {
      find_ml();
      MainLoopItem *item = find_main_loop(env,ml);
      item->Prepare();
      firsttime = false;
    }
    find_ml();
    MainLoopItem *item = find_main_loop(env,ml);
    item->execute(e);
  }
  virtual void handle_event(MainLoopEvent &e)
  {
    if (disabled) return;
    find_ml();
    MainLoopItem *item = find_main_loop(env,ml);
    item->handle_event(e);
  }
    
  virtual std::vector<int> shader_id() { 
    find_ml();
    MainLoopItem *item = find_main_loop(env,ml);
    return item->shader_id();
  }
private:
  GameApi::Env &env;
  Material *mat;
  GameApi::P p;
  GameApi::PTS pts;
  GameApi::ML ml;
  bool firsttime;
  bool disabled=false;
};

