std::string remove_postfix(std::string url)
{
  int s = url.size();
  int pos=-1;
  for(int i=0;i<s;i++)
    {
      if (url[i]=='/') pos=i;
    }
  if (pos==-1) return url;
  return url.substr(0,pos);
}
std::string remove_postfix2(std::string url)
{
  if (url[url.size()-1]=='\n'||url[url.size()-1]=='\r') return url.substr(0,url.size()-1);
  return url;;
}
