std::vector<std::string> split_str(std::string str, char ch)
{
  std::vector<std::string> res;
  int s = str.size();
  int last = 0;
  for(int i=0;i<s;i++)
    {
      char c = str[i];
      if (c==ch) {
	std::string spl = str.substr(last,i-last);
	res.push_back(spl);
	last = i+1;
      }
    }
  std::string spl = str.substr(last);
  res.push_back(spl);
  return res;
}

std::string ch_str(std::vector<std::string> vec, int i)
{
  int max = vec.size();
  if (i>=max) return "";
  return vec[i];
}

