class LoadFont : public FontInterface
{
public:
  LoadFont(GameApi::Env &e, std::string url, std::string homepageurl) : e(e), url(url), homepageurl(homepageurl) { firsttime = true; }
  void Collect(CollectVisitor &vis)
  {
    vis.register_obj(this);
  }
  void HeavyPrepare() { Prepare(); }

  virtual void Prepare() { 
    if (firsttime) {
      firsttime = false;
#ifndef EMSCRIPTEN
      e.async_load_url(url, homepageurl);
#endif
      GameApi::ASyncVec *vec = e.get_loaded_async_url(url);
      if (!vec) { std::cout << "async not ready!" << std::endl; return; }
      std::string s(vec->begin(), vec->end());
      std::stringstream ss(s);
      int count = -1;
      long idx = -1;
      int top = -1;
      int sx = -1;
      int sy = -1;
      int advance_x=-1;
      ss >> count;
      //std::cout << "Loading font: ";
      for(int i=0;i<count;i++)
	{
	  ss >> idx >> top >> sx >> sy >> advance_x;
	  //std::cout << idx;
	  int *ptr = new int[sx*sy];
	  for(int y=0;y<sy;y++) {
	    for(int x=0;x<sx;x++) {
	    int val = 0;
	    ss >> val;
	    ptr[x+y*sx] = val;
	    }
	  }
	  m_idx.push_back(idx);
	  m_top.push_back(top);
	  m_sx.push_back(sx);
	  m_sy.push_back(sy);
	  m_data.push_back(ptr);
	  m_advance_x.push_back(advance_x);
	}
      //std::cout << std::endl;
    }
  }
  virtual int Top(long idx) const { 
    int index = find_index(idx);
    if (index!=-1) {
      return m_top[index];
    }
    return 0;
  }
  virtual int SizeX(long idx) const
  {
    int index = find_index(idx);
    if (index!=-1) {
      return m_sx[index];
    }
    return 0;
  }
  virtual int SizeY(long idx) const
  {
    int index = find_index(idx);
    if (index!=-1) {
      return m_sy[index];
    }
    return 0;
  }
  virtual int AdvanceX(long idx) const 
  {
    int index = find_index(idx);
    if (index!=-1) {
      return m_advance_x[index];
    }
    return 0;
  }
  virtual int Map(long idx, int x, int y) const
  {
    int sx = SizeX(idx);
    int sy = SizeY(idx);
    int index = find_index(idx);
    if (index==-1) return 0;
    int *ptr = m_data[index];
    if (x>=0&&x<sx)
      if (y>=0&&y<sy)
	return ptr[x+y*sx];
    return 0;
  }

  int find_index(long idx) const
  {
    int s = m_idx.size();
    for(int i=0;i<s;i++)
      if (m_idx[i]==idx) return i;
    return -1;
  }

private:
  GameApi::Env &e;
  std::string url;
  std::string homepageurl;
  std::vector<long> m_idx;
  std::vector<int> m_top;
  std::vector<int> m_sx;
  std::vector<int> m_sy;
  std::vector<int*> m_data;
  std::vector<int> m_advance_x;
  bool firsttime;
};

